type Event = 'click' | 'hover' | 'focus';
type Handler = `on${Capitalize<Event>}`; // 'onClick' | 'onHover' | 'onFocus'
type CSSProperty = `${string}-${string}`; // aceita 'font-size', 'margin-top', etc.
type Getter<T> = `get${Capitalize<T & string>}`;

function Timestamped<T extends Constructor>(Base: T) { return class extends Base { createdAt = new Date(); }; }
function Activatable<T extends Constructor>(Base: T) { return class extends Base { active = false; }; }
class User extends Timestamped(Activatable(BaseEntity)) {} — User ganha createdAt e active.

